import React, { Component } from 'react';
import testTodoListData from './TestTodoListData.json'
import HomeScreen from './components/home_screen/HomeScreen'
import ItemScreen from './components/item_screen/ItemScreen'
import ListScreen from './components/list_screen/ListScreen'
import ModalScreen from './components/modal_screen/ModalScreen'

const AppScreen = {
  HOME_SCREEN: "HOME_SCREEN",
  LIST_SCREEN: "LIST_SCREEN",
  ITEM_SCREEN: "ITEM_SCREEN",
  MODAL_SCREEN: "MODAL_SCREEN"
}

class App extends Component {
  state = {
    currentScreen: AppScreen.HOME_SCREEN,
    todoLists: testTodoListData.todoLists,
    currentList: null,
    currentItem: null,
    modalIsVisible: false
  }

  showModal = () => {
    this.setState({modalIsVisible: true});
  }

  hideModal = () => {
    this.setState({modalIsVisible: false});
  }

  deleteList = (list) => {
    this.hideModal();
    this.removeFromLists(list);
    this.goHome();
  }

  goHome = () => {
    this.updateListChanges(this.state.currentList);
    this.setState({currentScreen: AppScreen.HOME_SCREEN});
    this.setState({currentList: null});
  }

  goItem = (item) => {
    this.setState({currentScreen: AppScreen.ITEM_SCREEN});
    this.setState({currentItem: item});
  }
  submitItem = (item) => {
    this.updateItem(item);
    this.exitItem();
  }

  exitItem = () => {
    this.setState({currentScreen: AppScreen.LIST_SCREEN});
    this.setState({currentItem: null});
  }

  updateItem = (newItem) => {
    let newList = {...this.state.currentList};
    let newItems = [...newList.items];
    if (newItem.key === newItems.length) { // If it is a new item
      newItems = [...newItems, newItem];
    }
    else { // If it is an existing item
      newItems = newItems.map(item => item.key === newItem.key ? newItem : item);
    }
    newList.items = newItems;
    this.setState({currentList: newList});
    this.updateListChanges(newList);
  }

  loadList = (todoListToLoad) => {
    this.setState({currentScreen: AppScreen.LIST_SCREEN});
    this.setState({currentList: todoListToLoad});
  }

  updateListChanges = (list) => {
    let targetKey = list.key;

    if (list.name.trim() === "") {
      list.name = "Unknown";
    }

    // Delete old version of list from lists, if it exists
    let newLists = [...this.state.todoLists].filter(list => list.key !== targetKey);

    // Add new version of list to top of lists
    newLists = [list, ...newLists];

    //Reorder keys
    newLists = this.updateIndices(newLists);

    // Update state
    this.setState({todoLists: newLists});
  }

  updateIndices = (lists) => {
    for(var i = 0; i < lists.length; i++) {
        lists[i].key = i;
    }
    return lists;
  }

  // Add to top of lists
  addToLists = (list) => {
    this.setState({todoLists: [list, ...this.state.todoLists]});
  }
  // Remove from lists
  removeFromLists = (key) => {
    this.setState({todoLists: [...this.state.todoLists.filter(list => list.key !== key)]});
  }

  render() {
    switch(this.state.currentScreen) {
      case AppScreen.HOME_SCREEN:
        return <HomeScreen 
        loadList={this.loadList.bind(this)} 
        todoLists={this.state.todoLists} />;
      case AppScreen.LIST_SCREEN:  
        if (this.state.modalIsVisible) {     
        return <ListScreen
          goHome={this.goHome.bind(this)}
          goItem={this.goItem.bind(this)}
          todoList={this.state.currentList} 
          updateListChanges={this.updateListChanges.bind(this)} />;
        }
        else {
          return <ListScreen
          goHome={this.goHome.bind(this)}
          goItem={this.goItem.bind(this)}
          todoList={this.state.currentList} 
          updateListChanges={this.updateListChanges.bind(this)} />; 
        }
      case AppScreen.ITEM_SCREEN:
        return <ItemScreen 
          currentScreen={this.state.currentScreen}
          todoItem={this.state.currentItem}
          submitItem={this.submitItem.bind(this)}
          exitItem={this.exitItem.bind(this)} />;
      default:
        return <div>ERROR</div>;
    }
  }
}

export default App;
