import React, { Component } from 'react'
import ListItemCard from './ListItemCard'

export class ListItemsTable extends Component {

    state = {
        items: this.props.todoList.items,
        taskSortToggle: false,
        dueDateSortToggle: false,
        completedSortToggle: false
    }

    updateIndices = (items) => {
        for(var i = 0; i < items.length; i++) {
            items[i].key = i;
        }
        return items;
    }
    sortByTask = () => {
        let newItems = [...this.state.items];
        if (this.state.taskSortToggle){
            newItems.sort((a, b) =>  a.description > b.description ? 1 : -1);
        }
        else{
            newItems.sort((a, b) =>  a.description > b.description ? -1 : 1);
        }
        newItems = this.updateIndices(newItems);
        this.setState({items: newItems});
        this.setState({taskSortToggle: !this.state.taskSortToggle});
        console.log(newItems);
    }

    sortByDueDate = () => {
        let newItems = [...this.state.items];
        if (this.state.dueDateSortToggle){
            newItems.sort((a, b) =>  a.due_date > b.due_date ? 1 : -1);
        }
        else{
            newItems.sort((a, b) =>  a.due_date > b.due_date ? -1 : 1);
        }
        newItems = this.updateIndices(newItems);
        this.setState({items: newItems});
        this.setState({dueDateSortToggle: !this.state.dueDateSortToggle});
    }

    sortByCompleted = () => {
        let newItems = [...this.state.items];
        if (this.state.completedSortToggle){
            newItems.sort((a, b) =>  a.completed > b.completed ? 1 : -1);
        }
        else{
            newItems.sort((a, b) =>  a.completed > b.completed ? -1 : 1);
        }
        newItems = this.updateIndices(newItems);
        this.setState({items: newItems});
        this.setState({completedSortToggle: !this.state.completedSortToggle});
    }

    swapItems = (items, key1, key2) => {
        let temp = items[key1];
        items[key1] = items[key2];
        items[key2] = temp;
        items = this.updateIndices(items);
        return items;
    }
    moveItemUp = (item) => {
        let newItems = [...this.state.items];
        if (item.key > 0){
            newItems = this.swapItems(newItems, item.key, newItems[item.key - 1]);
        }
        this.setState({items: newItems});
    }

    moveItemDown = (item) => {
        let newItems = [...this.state.items];
        if (item.key < newItems.length - 1){
            newItems = this.swapItems(newItems, item.key, newItems[item.key + 1]);
        }
        this.setState({items: newItems});
    }

    deleteItem = (item) => {
        let newItems = [...this.state.items].filter(x => x.key !== item.key);
        this.setState({items: newItems});
    }

    render() {
        console.log(this.state.items);
        return (
            <div id="list_items_container">
                <div className="list_item_header_card">
                    <div className="list_item_task_header" onClick={this.sortByTask}>Task</div>
                    <div className="list_item_due_date_header" onClick={this.sortByDueDate}>Due Date</div>
                    <div className="list_item_status_header" onClick={this.sortByCompleted}>Status</div>
                </div>
                {
                    this.state.items.map((todoItem)=>(
                        <ListItemCard 
                            key={todoItem.key}
                            listItem={todoItem}
                            isFirst={todoItem.key === 0}
                            isLast={todoItem.key === this.state.items.length - 1}
                            moveItemUp={this.moveItemUp.bind(this)}
                            moveItemDown={this.moveItemDown.bind(this)}
                            deleteItem={this.deleteItem.bind(this)} 
                            />
                    ))
                }
            </div>
        )
    }
}

export default ListItemsTable
