import React, { Component } from 'react'

export class ListItemCard extends Component {

    state = {
        item: this.props.listItem
    }
    getCompleted = () => {
        return(this.props.listItem.completed ? 'list_item_card_completed' : 'list_item_card_not_completed');
    }

    getMoveUp = () => {
        return(this.props.isFirst ? 'disabled' : 'list_item_card_button');
    }

    getMoveDown = () => {
        return(this.props.isLast ? 'disabled' : 'list_item_card_button');
    }

    render() {
        return (
            <div className='list_item_card'>
                <div className='list_item_card_description'>
                    {this.props.listItem.description}
                </div>
                <div className='list_item_card_assigned_to'>
                    Assigned To: <strong>{this.props.listItem.assigned_to}</strong>
                </div>
                <div className='list_item_card_due_date'>
                    {this.props.listItem.due_date}
                </div>
                <div className={this.getCompleted()}>
                    {this.props.listItem.completed ? "Completed" : "Pending"}
                </div>
                <div className='list_item_card_toolbar'>
                    <div className={this.getMoveUp()} onClick={this.props.moveItemUp.bind(this, this.state.item)}>&#11014;</div>
                    <div className={this.getMoveDown()} onClick={this.props.moveItemDown.bind(this, this.state.item)}>&#11015;</div>
                    <div className='list_item_card_button' onClick={this.props.deleteItem.bind(this, this.state.item)}>&#10006;</div>
                </div> 
            </div>
        )
    }
}

export default ListItemCard
