import React, { Component } from 'react'

export class ListItemCard extends Component {

    getCompleted = () => {
        return(this.props.listItem.completed ? 'list_item_card_completed' : 'list_item_card_not_completed');
    }

    getMoveUp = () => {
        return(this.props.isFirst ? 'disabled list_item_card_button' : 'list_item_card_button');
    }

    getMoveDown = () => {
        return(this.props.isLast ? 'disabled list_item_card_button' : 'list_item_card_button');
    }

    getItem = () => {
        let key = this.props.listItem.key;
        let description = this.props.listItem.description;
        let assigned_to = this.props.listItem.assigned_to;
        let due_date = this.props.listItem.due_date;
        let completed = this.props.listItem.completed;

        return {key, description, assigned_to, due_date, completed};
    }

    moveItemUp = (e) => {
        this.props.moveItemUp.bind(this, this.props.listItem);
    }

    moveItemDown = (e) => {
        e.stopPropagation();
        this.props.moveItemDown.bind(this, this.props.listItem);
    }

    deleteItem = (e) => {
        e.stopPropagation();
        this.props.deleteItem.bind(this, this.props.listItem);
    }

    render() {
        return (
            <div className='list_item_card' onClick={this.props.goItem.bind(this, this.getItem())}>
                <div className='list_item_card_description'>
                    {this.props.listItem.description}
                </div>
                <div className='list_item_card_assigned_to'>
                    Assigned To: <strong>{this.props.listItem.assigned_to}</strong>
                </div>
                <div className='list_item_card_due_date'>
                    {this.props.listItem.due_date}
                </div>
                <div className={this.getCompleted()}>
                    {this.props.listItem.completed ? "Completed" : "Pending"}
                </div>
                <div className='list_item_card_toolbar'>
                    <div className={this.getMoveUp()} onClick={this.moveItemUp(e)}>&#11014;</div>
                    <div className={this.getMoveDown()} onClick={this.moveItemDown(e)}>&#11015;</div>
                    <div className='list_item_card_button' onClick={this.deleteItem(e)}>&#10006;</div>
                </div>
            </div>
        )
    }
}

export default ListItemCard
