            newItems.sort((a, b) =>  a.due_date > b.due_date ? -1 : 1);
        }
        newItems = this.updateIndices(newItems);
        this.setState({items: newItems});
        this.setState({dueDateSortToggle: !this.state.dueDateSortToggle});
