import React, { Component } from 'react'
import PropTypes from 'prop-types';

export class ItemScreen extends Component {
    state = {
        description: this.todoItem.description,
        assigned_to: this.todoItem.assigned_to,
        due_date: this.todoItem.due_date,
        completed: this.todoItem.completed
    }

    setDescription = (e) => {
        this.setState({description: e.target.value});
    }

    collectChanges = () => {

    }

    render() {
        return (
            <div id="todo_item">
                <div id="item_heading">
                    <span>Item</span>
                </div>
                <div id="item_form_container">
                    <span id="item_description_prompt" className="item_prompt">Description: </span>
                    <input type="text" id="item_description_textfield" className="item_input" value={this.props.todoItem.description}/>
                    <p className="item_spacing"> </p>
                    <span id="item_assigned_to_prompt" className="item_prompt">Assigned To: </span>
                    <input type="text" id="item_assigned_to_textfield" className="item_input" value={this.props.todoItem.assigned_to}/>
                    <p className="item_spacing"> </p>
                    <span id="item_due_date_prompt" className="item_prompt">Due Date: </span>
                    <input type="date" id="item_due_date_picker" className="item_input" value={this.props.todoItem.due_date}/>
                    <p className="item_spacing"> </p>
                    <span id="item_completed_prompt" className="item_prompt">Completed: </span>
                    <input type="checkbox" id="item_completed_checkbox" className="item_input" checked={this.props.todoItem.completed}/>
                </div>
                <div id="item_buttons_container">
                    <button id="item_form_submit_button" className="item_buttons" onClick={this.props.submitItem.bind(this, this.collectChanges())}>Submit</button>
                    <button id="item_form_cancel_button" className="item_buttons" onClick={this.props.exitItem.bind(this)}>Cancel</button>
                </div>
            </div>
        )
    }
}

ItemScreen.propTypes = {
    currentScreen: PropTypes.string.isRequired,
    todoItem: PropTypes.object.isRequired
}

export default ItemScreen
